import styled, { css } from "styled-components";
import colors from "../../utils/colors";
import { useContext } from "react";
import { ThemeContext } from '../../utils/context'

const FooterStyled = styled.div`
    display: flex;
    flex-direction: row;
    align-items: end;
    justify-content: center;
    padding-top: 60px;
`;

const NightModeButton = styled.div`
    position: relative;
    border: none;
    cursor: pointer;
    border-radius: 3px;
    width: 70px;
    height: 32px;
    margin: 50px;
    background-color: ${colors.secondary};
`;

const Button = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    box-sizing: border-box;
    width: 38px;
    cursor: pointer;
    background-color: ${colors.primary};
    box-shadow: 0 2px 4px rgb(0, 0, 0, 0.25);
    border-radius: 3px;
    padding: 8px 12px;
    position: absolute;
    transition: all 0.2s ease; 
    line-height: 16px;
    left: ${({ isDarkMode }) =>
      (isDarkMode ? '0px' : '32px')};
`;

const Link = styled.link`
    marging: 10px;
    color: ${({ isDarkMode }) =>
    (isDarkMode ? `${colors.secondary}` : `${colors.primary}`)};
`;

function Footer() {
    const { toggleTheme, theme } = useContext(ThemeContext)
    return(
                <FooterStyled>
                    <NightModeButton onClick={() => toggleTheme()} isDarkMode={theme === 'light'}>
                        <Button isDarkMode={theme === 'dark'}>{theme === 'light' ? '‚òÄÔ∏è' : 'üåô'}</Button>
                    </NightModeButton>
                    <Link to= "/">about us</Link>
                    <Link to= "/">contact</Link>
                </FooterStyled>
    )
}

export default Footer
