import { Link } from 'react-router-dom'
import styled from 'styled-components'
import color from '../../utils/Styles/colors'
import image from '../../Assets/dark-logo.png'

function Header() {
    const StyledLink = styled(Link)`
    padding: 15px;
    color: ${color.primary};
    text-decoration: none;
    font-size: 18px
    ${(props) =>
        props.$isFullLink &&
        `font-color: white; color: white; border-radius: 30px; margin-left: auto; height 40px; background-color: ${color.primary};`}
`

    const StyledHeader = styled.div`
    text-align: right;
    padding: 32;
    display: flex;
    flex-direction: row;
`

    const StyledLogo = styled.img`
    width: 150px;
    height: 60px;
    display: flex;
    justify-content: left;
`

    return (
        <StyledHeader>
            <StyledLogo src={image} alt='logo'></StyledLogo>
            <StyledLink to= "/" $isFullLink> Accueil</StyledLink>
            <StyledLink to="/survey/1" >Questionnaire</StyledLink>
            <StyledLink to="/freelance">freelances</StyledLink>
        </StyledHeader>
    )
}

export default Header
