import image from "../../Assets/home-illustration.svg";
import styled, { keyframes } from "styled-components";
import { ThemeContext } from "../../utils/context";
import { useContext } from "react";

const Cont = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 7em;
  justify-content: center;
  text-align: center;
`;

const StyledPara = styled.p`
  max-width: 300px;
  font-size: 25px;
  margin: 25px;
  color: ${({ theme }) => theme.text};
`;

const StyledHeader = styled.h1`
  font-size: 3em;
  color: ${({ theme }) => theme.primary};
`;

const Button = styled.button`
  padding: 10px 20px;
  font-size: 1em;
  margin-top: 20px;
  cursor: pointer;
  background-color: ${({ theme }) => theme.primary};
  color: white;
  border: none;
  border-radius: 5px;
  box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
  transition: background-color 0.3s;

  &:hover {
    background-color: ${({ theme }) => theme.secondary};
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const Image = styled.img`
  max-width: 100%;
  height: auto;
  animation: ${fadeIn} 2.5s ease-in;
  margin-top: 20px;
`;

function Home() {
  const { toggleTheme, theme } = useContext(ThemeContext);

  return (
    <Cont>
      <StyledHeader>Bienvenue chez Nous</StyledHeader>
      <StyledPara>Repérez vos besoins, on s’occupe du reste, avec les meilleurs talents</StyledPara>
      <Image src={image} alt="Home Illustration" />
      <Button onClick={toggleTheme}>Changer de Thème</Button>
    </Cont>
  );
}

export default Home;
