import { Link, useLocation } from 'react-router-dom';
import styled from 'styled-components';
import color from '../../utils/colors';
import image from '../../Assets/dark-logo.png';
import imagelight from '../../Assets/light-logo.png';
import { ThemeContext } from '../../utils/context';
import { useContext } from 'react';

const StyledLink = styled(Link)`
  padding: 1em;
  color: ${({ isDarkMode }) => (isDarkMode ? `${color.secondary}` : `${color.primary}`)};
  text-decoration: none;
  font-size: 1rem;
  position: relative;
  z-index: 1;

  ${(props) =>
    props.$isFullLink &&
    `
      color: white;
      height: 20px;
      border-radius: 30px;
      margin-left: auto;
      background-color: ${color.primary};
  `}

  @media (max-width: 768px) {
    font-size: 0.875rem;
    padding: 0.75em;
  }
`;

const StyledHeader = styled.div`
  text-align: right;
  padding: 1em;
  display: flex;
  flex-direction: row;
  align-items: center;
  position: relative;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: flex-start;
  }
`;

const StyledLogo = styled.img`
  width: 10rem;
  height: auto;
  margin-right: auto;

  @media (max-width: 768px) {
    margin-right: 0;
    margin-bottom: 1em;
  }
`;

const Indicator = styled.div`
  position: absolute;
  bottom: 0;
  left: ${({ position }) => position}%;
  width: 5rem; /* Ajustez la largeur selon vos besoins */
  height: 0.25rem; /* Ajustez la hauteur selon vos besoins */
  background-color: ${({ isDarkMode }) => (isDarkMode ? `${color.secondary}` : `${color.primary}`)};
  transition: left 0.3s ease;
  z-index: 0;

  @media (max-width: 768px) {
    width: 4rem;
    height: 0.2rem;
  }
`;

function Header() {
  const { toggleTheme, theme } = useContext(ThemeContext);
  const location = useLocation();

  const links = [
    { path: '/', label: 'Accueil' },
    { path: '/survey/1', label: 'Questionnaire' },
    { path: '/freelance', label: 'Freelances' }
  ];

  const activeIndex = links.findIndex(link => link.path === location.pathname);
  const indicatorPosition = (activeIndex * 33.33) + 1.66; // Calcul en pourcentage pour être plus flexible

  return (
    <StyledHeader>
      <StyledLogo src={theme === 'dark' ? image : imagelight} alt='logo' />
      {links.map((link, index) => (
        <StyledLink
          key={link.path}
          isDarkMode={theme === 'dark'}
          to={link.path}
          $isFullLink={link.path === location.pathname}
        >
          {link.label}
        </StyledLink>
      ))}
      <Indicator position={indicatorPosition} isDarkMode={theme === 'dark'} />
    </StyledHeader>
  );
}

export default Header;
